import React, { useState, useEffect } from "react";

const SimpleInput = (props) => {
  const [inputName, setInputName] = useState("");
  const [isValidName, setIsValidName] = useState(true);
  const [formKecerlidiseSubmitOlsun, setformKecerlidiseSubmitOlsun] = useState(false);

  useEffect(() => {
    if (isValidName) 
    {
      setformKecerlidiseSubmitOlsun(false)
    }
     else 
    {
      setformKecerlidiseSubmitOlsun()
    }
  }, [isValidName]);

  const changeInputName = (e) => {
    setInputName(e.target.value);

    if (inputName.trim() !== "") {
      setIsValidName(true);
    }
  };

  const submitHandler = (event) => {
    event.preventDefault();

    setInputName("");

    if (inputName.trim() === "") {
      setIsValidName(false);
      return;
    } else {
      setIsValidName(true);
    }
  };

  const changeClassesforValidation = isValidName
    ? "form-control"
    : "form-control invalid";

  return (
    <form onSubmit={submitHandler}>
      <div className={changeClassesforValidation}>
        <label htmlFor="name">Your Name</label>

        <input
          value={inputName}
          onChange={changeInputName}
          type="text"
          id="name"
        />

        {!isValidName && <p style={{ color: "red" }}>Name must not be empty.</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
